"use client";
import { ReactLenis, useLenis } from 'lenis/react'
import { SectionHero } from "./SectionHero";
import dynamic from 'next/dynamic';

const SectionFooter = dynamic(() => import("./SectionFooter").then((mod) => mod.SectionFooter), { ssr: false });
const SectionTestimonials = dynamic(() => import("./SectionTestimonials").then((mod) => mod.SectionTestimonials), { ssr: false });
const SectionTechstack = dynamic(() => import("./SectionTechstack").then((mod) => mod.SectionTechstack), { ssr: false });
const SectionFlower = dynamic(() => import("./SectionFlower").then((mod) => mod.SectionFlower), { ssr: false });
const SectionServices = dynamic(() => import("./SectionServices").then((mod) => mod.SectionServices), { ssr: false });
const SectionProjects = dynamic(() => import("./SectionProjects").then((mod) => mod.SectionProjects), { ssr: false });
const SectionProjectsMobile = dynamic(() => import("./SectionProjectsMobile").then((mod) => mod.SectionProjectsMobile), { ssr: false });
const SectionKPI = dynamic(() => import("./SectionKPI").then((mod) => mod.SectionKPI), { ssr: false });

import "./main.css";
import { useEffect, useLayoutEffect, useState } from 'react';
import { useProgress } from "@react-three/drei";

const Main = () => {

  const { progress } = useProgress();
  const [fadeOut, setFadeOut] = useState(false);
  const lenis = useLenis();

  useLayoutEffect(() => {
    if (progress === 100) {
      setFadeOut(true);
      lenis?.start();
    }
  }, [progress, lenis]);

  return (
    <ReactLenis root>
      <div className={`initial-loading-screen ${fadeOut ? "fade-out" : ""}`} >
        <div className="loading-image-box" >
          <img src="/images/loading.gif" className="loading-image" alt="Loading Image" />
        </div>
      </div>
      <SectionHero />

      <div className="border-padding">
        <div className="section-border"></div>
      </div>
      <SectionServices />
      <div className="normal-padding" />
      <SectionProjects />
      <SectionProjectsMobile />
      <div className="normal-padding" />
      <SectionTechstack />
      <div className="normal-padding" />
      <SectionTestimonials />
      <div className="normal-padding" />
      <SectionKPI />
      <div className="normal-padding" />
      <SectionFlower />
      <div className="normal-padding" />
      <SectionFooter />
    </ReactLenis>
  );
};

export default Main;

/* eslint-disable react/jsx-key */
import React, { useEffect, useRef } from "react";
import gsap from "gsap";
import SplitText from "gsap/src/SplitText";
import ScrollTrigger from "gsap/ScrollTrigger";

gsap.registerPlugin(SplitText, ScrollTrigger);

export const SectionFlower = () => {

  useEffect(() => {
    let frameCount = 300,
      urls = new Array(frameCount).fill().map((o, i) => `/imageSequence/image${i + 1}.webp`);

    let loaded = false;

    ScrollTrigger.create({
      trigger: ".flower",
      start: "top bottom+=500", // Start loading when the section is 500px from entering viewport
      onEnter: () => {
        if (!loaded) {
          loaded = true;
          imageSequence({
            urls,
            canvas: "#image-sequence",
            scrollTrigger: {
              trigger: ".flower",
              start: "top bottom",
              end: "bottom top",
              scrub: true,
            }
          });
        }
      }
    });

    function imageSequence(config) {
      let playhead = { frame: 0 },
        canvas = gsap.utils.toArray(config.canvas)[0],
        ctx = canvas.getContext("2d"),
        curFrame = -1,
        onUpdate = config.onUpdate,
        images,
        updateImage = function () {
          let frame = Math.round(playhead.frame);
          if (frame !== curFrame) {
            config.clear && ctx.clearRect(0, 0, canvas.width, canvas.height);
            ctx.drawImage(images[Math.round(playhead.frame)], 0, 0);
            curFrame = frame;
            onUpdate && onUpdate.call(this, frame, images[frame]);
          }
        };
      images = config.urls.map((url, i) => {
        let img = new Image();
        img.src = url;
        i || (img.onload = updateImage);
        return img;
      });
      return gsap.to(playhead, {
        frame: images.length - 1,
        ease: "none",
        onUpdate: updateImage,
        duration: images.length / (config.fps || 30),
        paused: !!config.paused,
        scrollTrigger: config.scrollTrigger
      });
    }
  }, []) // Empty dependency array ensures this only runs once on mount

  const imageRef1 = useRef();
  const imageRef2 = useRef();

  const textRef1 = useRef();
  const textRef2 = useRef();
  const textRef3 = useRef();
  const textRef4 = useRef();
  const textRef5 = useRef();
  const textRef6 = useRef();
  const textRef7 = useRef();
  const textRef8 = useRef();

  useEffect(() => {

    gsap.fromTo(imageRef1.current, { width: 0, opacity: 0 }, { width: "5vw", opacity: 1, duration: 1, scrollTrigger: { trigger: imageRef1.current, start: "top 95%" } })
    gsap.fromTo(imageRef2.current, { width: 0, opacity: 0 }, { width: "5vw", opacity: 1, duration: 1, scrollTrigger: { trigger: imageRef2.current, start: "top 95%" } })

    const textRefSplit1 = new SplitText(textRef1.current, { type: "chars" });
    const textRefSplit2 = new SplitText(textRef2.current, { type: "chars" });
    const textRefSplit3 = new SplitText(textRef3.current, { type: "chars" });
    const textRefSplit4 = new SplitText(textRef4.current, { type: "chars" });
    const textRefSplit5 = new SplitText(textRef5.current, { type: "chars" });
    const textRefSplit6 = new SplitText(textRef6.current, { type: "chars" });
    const textRefSplit7 = new SplitText(textRef7.current, { type: "chars" });
    const textRefSplit8 = new SplitText(textRef8.current, { type: "chars" });

    gsap.fromTo(textRefSplit1.chars, { opacity: 0.25 }, { delay: 0, opacity: 1, duration: 0.5, stagger: 0.1, scrollTrigger: { trigger: textRef1.current, start: "top 95%" } })
    gsap.fromTo(textRefSplit2.chars, { opacity: 0.25 }, { delay: 0.25, opacity: 1, duration: 0.5, stagger: 0.1, scrollTrigger: { trigger: textRef2.current, start: "top 95%" } })
    gsap.fromTo(textRefSplit3.chars, { opacity: 0.25 }, { delay: 0.5, opacity: 1, duration: 0.5, stagger: 0.1, scrollTrigger: { trigger: textRef3.current, start: "top 95%" } })

    gsap.fromTo(textRefSplit4.chars, { opacity: 0.25 }, { delay: 0, opacity: 1, duration: 0.5, stagger: 0.1, scrollTrigger: { trigger: textRef4.current, start: "top 95%" } })
    gsap.fromTo(textRefSplit5.chars, { opacity: 0.25 }, { delay: 0.6, opacity: 1, duration: 0.5, stagger: 0.1, scrollTrigger: { trigger: textRef5.current, start: "top 95%" } })
    gsap.fromTo(textRefSplit6.chars, { opacity: 0.25 }, { delay: 0.85, opacity: 1, duration: 0.5, stagger: 0.1, scrollTrigger: { trigger: textRef6.current, start: "top 95%" } })

    gsap.fromTo(textRefSplit7.chars, { opacity: 0.25 }, { delay: 0, opacity: 1, duration: 0.5, stagger: 0.1, scrollTrigger: { trigger: textRef7.current, start: "top 95%" } })
    gsap.fromTo(textRefSplit8.chars, { opacity: 0.25 }, { delay: 0.25, opacity: 1, duration: 0.5, stagger: 0.1, scrollTrigger: { trigger: textRef8.current, start: "top 95%" } })

  }, [])

  return (
    <section className="flower">
      <div className="flower-content">
        <div className="projects-gradient-top" />
        <div className="projects-gradient-bottom" />
        <div className="flower-content-sequence">
          <canvas className="image-sequence-canvas" id="image-sequence" width="1920" height="1080" />
        </div>
        <div className="flower-content-textbox">
          <div className="flower-content-textbox-item" >
            <span>
              <h2 className="subheadline white" ref={textRef1} >Grow</h2>
            </span>
            <span>
              <h2 className="subheadline white" ref={textRef2} >Your</h2>
            </span>
            <span>
              <h2 className="subheadline white" ref={textRef3} >Digital</h2>
            </span>
            <span>
              <div className="flower-content-right-content-item" ref={imageRef1} >
                <img src="/images/iphoneoptimized.png" className="flower-content-right-content-item-image" alt="" />
              </div>
            </span>
          </div>
          <div className="flower-content-textbox-item" >
            <span>
              <h2 className="subheadline white" ref={textRef4} >Presence,</h2>
            </span>
            <span>
              <h2 className="subheadline white" ref={textRef5} >Let</h2>
            </span>
            <span>
              <h2 className="subheadline white" ref={textRef6} >Your</h2>
            </span>
          </div>
          <div className="flower-content-textbox-item" >
            <span>
              <h2 className="subheadline white" ref={textRef7} >Vision</h2>
            </span>
            <span>
              <div className="flower-content-right-content-item" ref={imageRef2} >
                <img src="/images/iphoneoptimized.png" className="flower-content-right-content-item-image" alt="" />
              </div>
            </span>
            <span>
              <h2 className="subheadline white" ref={textRef8} >Bloom</h2>
            </span>
          </div>
        </div>
      </div>
    </section>
  );
};

/* eslint-disable react/jsx-key */
"use client";
import React, { Suspense, useEffect, useLayoutEffect, useRef, useState } from "react";
import dynamic from 'next/dynamic'
import gsap from "gsap";
import SplitText from "gsap/src/SplitText";
import ScrollTrigger from "gsap/ScrollTrigger";
import Marquee from "react-fast-marquee";
import { Hand, Star } from "lucide-react";
import useEmblaCarousel from "embla-carousel-react";
import { PrevButton, NextButton, usePrevNextButtons } from "./Carousel/EmblaCarouselArrowButtons"
import { DotButton, useDotButton } from './Carousel/EmblaCarouselDotButton'
import Fade from 'embla-carousel-fade'
import Image from "next/image";

gsap.registerPlugin(SplitText, ScrollTrigger);

export const SectionProjectsMobile = () => {

  const subheadlineBoxRef = useRef()
  const titleRef = useRef()
  const descriptionRef = useRef()
  const contentRef = useRef();
  const imageContainerRef = useRef();

  // GSAP ANIMATIONS

  useEffect(() => {

    // subheadline box animation
    gsap.to(subheadlineBoxRef.current, { opacity: 1, filter: 'blur(0px)', duration: 0.5, ease: 'power1', scrollTrigger: { trigger: subheadlineBoxRef.current, start: "top 95%" } });

    // headline text animation
    const titleSplit = new SplitText(titleRef.current, { type: "words" });
    gsap.fromTo(titleSplit.words, { 'will-change': 'opacity, transform', filter: 'blur(8px)', opacity: 0, yPercent: 50 }, { opacity: 1, filter: 'blur(0px)', yPercent: 0, stagger: 0.05, duration: 0.75, ease: "power2", scrollTrigger: { trigger: titleRef.current, start: "top 95%" } });

    // description text animation
    const descriptionSplit = new SplitText(descriptionRef.current, { type: "words" });
    gsap.fromTo(descriptionSplit.words, { filter: 'blur(8px)', opacity: 0 }, { opacity: 1, filter: 'blur(0px)', stagger: 0.025, ease: 'sine', scrollTrigger: { trigger: descriptionRef.current, start: "top 95%" } });

    // image parallax effect
    gsap.fromTo(imageContainerRef.current, { y: "10vw" }, { y: "-10vw", scrollTrigger: { trigger: ".projects", start: "top bottom", end: "bottom top", scrub: true } })
  }, [])

  // EMBLA CAROUSEL

  const [emblaRef, emblaApi] = useEmblaCarousel({ loop: true }, [Fade()])

  const { selectedIndex, scrollSnaps, onDotButtonClick } = useDotButton(emblaApi)

  const {
    prevBtnDisabled,
    nextBtnDisabled,
    onPrevButtonClick,
    onNextButtonClick
  } = usePrevNextButtons(emblaApi)


  return (
    <section className="projects projects-mobile">
      <div className="textbox">
        <div className="subheadline-box opacity-blur" ref={subheadlineBoxRef} >
          <Star className="subheadline-box-icon" />
          <h2 className="small-description grey" >Featured Works</h2>
        </div>
        <div className="titlebox">
          <div className="titlebox-big-gradient" />
          <h2 className="subheadline white" ref={titleRef} >Pioneering Projects That Consistently <br className="hide-on-mobile" /> Redefine What's Possible</h2>
        </div>
        <p className="description grey" ref={descriptionRef} >Transforming startups, SMEs, and industry <br className="hide-on-desktop" /> giants into digital leaders.</p>
      </div>
      <div className="projects-content" ref={contentRef} onClick={onNextButtonClick} >
        <div className="projects-gradient-top" />
        <div className="projects-gradient-bottom" />
        <div className="project-content-wrapper" ref={imageContainerRef} >
          <div className="projects-carousel" ref={emblaRef} >
            <div className="projects-carousel-row">
              <div className="projects-carousel-item">
                <Image src="/mockups/heave.webp" width={1920} height={1080} className="projects-carousel-item-image" alt="Heave - Apex Gym Website by Quroix Labs" />
              </div>
              <div className="projects-carousel-item">
                <Image src="/mockups/essentia.webp" width={1920} height={1080} className="projects-carousel-item-image" alt="Essentia - Artist Portfolio Website by Quroix Labs" />
              </div>
              <div className="projects-carousel-item">
                <Image src="/mockups/kinimatic.webp" width={1920} height={1080} className="projects-carousel-item-image" alt="Kinimatic - SMMA Agency Website by Quroix Labs" />
              </div>
              <div className="projects-carousel-item">
                <Image src="/mockups/peak.webp" width={1920} height={1080} className="projects-carousel-item-image" alt="Peak - Wholesale Product Catalogue by Quroix Labs" />
              </div>
              <div className="projects-carousel-item">
                <Image src="/mockups/vitalenta.webp" width={1920} height={1080} className="projects-carousel-item-image" alt="Vitalenta - Aura Luxury Salon Website by Quroix Labs" />
              </div>
              <div className="projects-carousel-item">
                <Image src="/mockups/rev.webp" width={1920} height={1080} className="projects-carousel-item-image" alt="Rev - Brim Clocks E-commerce by Quroix Labs" />
              </div>
            </div>
          </div>
        </div>
        <div className="embla__dots">
          {scrollSnaps.map((_, index) => (
            <DotButton
              key={index}
              onClick={() => onDotButtonClick(index)}
              className={'embla__dot'.concat(
                index === selectedIndex ? ' embla__dot--selected' : ''
              )}
            />
          ))}
        </div>
      </div>
    </section>
  );
};

/* eslint-disable react/jsx-key */
import React, { Suspense, useCallback, useEffect, useLayoutEffect, useRef, useState } from "react";
import gsap from "gsap";
import SplitText from "gsap/src/SplitText";
import ScrollTrigger from "gsap/ScrollTrigger";
import { ArrowUpRight, ChevronRight, Hand, Layers, Link, Send } from "lucide-react";
import useEmblaCarousel from "embla-carousel-react";
import { PrevButton, NextButton, usePrevNextButtons } from "./Carousel/EmblaCarouselArrowButtons"
import { DotButton, useDotButton } from './Carousel/EmblaCarouselDotButton'
import { motion, AnimatePresence } from "framer-motion";

gsap.registerPlugin(SplitText, ScrollTrigger);

const TWEEN_FACTOR_BASE = 0.25

const numberWithinRange = (number, min, max) => Math.min(Math.max(number, min), max)

export const SectionTechstack = () => {

    // GSAP ANIMATIONS

    const subheadlineBoxRef = useRef()
    const titleRef = useRef()
    const descriptionRef = useRef()
    const bentoBoxRef1 = useRef()
    const bentoBoxRef2 = useRef()
    const bentoBoxRef3 = useRef()

    useEffect(() => {

        // subheadline box animation
        gsap.to(subheadlineBoxRef.current, { opacity: 1, filter: 'blur(0px)', duration: 0.5, ease: 'power1', scrollTrigger: { trigger: subheadlineBoxRef.current, start: "top 95%" } });

        // headline text animation
        const titleSplit = new SplitText(titleRef.current, { type: "words" });
        gsap.fromTo(titleSplit.words, { 'will-change': 'opacity, transform', filter: 'blur(8px)', opacity: 0, yPercent: 50 }, { opacity: 1, filter: 'blur(0px)', yPercent: 0, stagger: 0.05, duration: 0.75, ease: "power2", scrollTrigger: { trigger: titleRef.current, start: "top 95%" } });

        // description text animation
        const descriptionSplit = new SplitText(descriptionRef.current, { type: "words" });
        gsap.fromTo(descriptionSplit.words, { filter: 'blur(8px)', opacity: 0 }, { opacity: 1, filter: 'blur(0px)', stagger: 0.025, ease: 'sine', scrollTrigger: { trigger: descriptionRef.current, start: "top 95%" } });

        // bento grid boxes animations
        gsap.fromTo(bentoBoxRef1.current, { rotationY: 30, scale: 0.6, opacity: 0 }, { rotationY: 0, scale: 1, opacity: 1, duration: 0.75, ease: 'power1', scrollTrigger: { trigger: bentoBoxRef1.current, start: "top bottom" } });
        gsap.fromTo(bentoBoxRef2.current, { rotationY: 30, scale: 0.6, opacity: 0 }, { rotationY: 0, scale: 1, opacity: 1, duration: 0.75, ease: 'power1', scrollTrigger: { trigger: bentoBoxRef2.current, start: "top bottom" } });
        const addClassnames = () => {
            const cards = document.querySelectorAll(".techstack-item-card");
            cards.forEach((card, index) => {
                card.classList.add(`techstack-item-card-animated-${index + 1}`);
            });
            setTimeout(() => {
                cards.forEach((card) => {
                    card.classList.add("techstack-item-card-animated-hover");
                });
            }, 1250);
        };
        gsap.fromTo(bentoBoxRef3.current, { rotationY: 30, scale: 0.6, opacity: 0 }, { delay: 0.2, rotationY: 0, scale: 1, opacity: 1, duration: 0.75, ease: 'power1', scrollTrigger: { trigger: bentoBoxRef3.current, start: "top bottom" }, onComplete: addClassnames });

    }, [])

    // VIDEO
    const videoRef = useRef(null);

    const handleVideoButtonClick = () => {
        if (videoRef.current) {
            videoRef.current.play();
        }
    };

    // EMBLA CAROUSEL

    const [emblaRef, emblaApi] = useEmblaCarousel({ loop: true, watchDrag: false });

    const { selectedIndex, scrollSnaps, onDotButtonClick } =
        useDotButton(emblaApi)

    const tweenFactor = useRef(0);
    const tweenNodes = useRef([]);

    const {
        prevBtnDisabled,
        nextBtnDisabled,
        onPrevButtonClick,
        onNextButtonClick,
    } = usePrevNextButtons(emblaApi);

    const setTweenNodes = useCallback((emblaApi) => {
        tweenNodes.current = emblaApi
            .slideNodes()
            .map((slideNode) =>
                slideNode.querySelector('.techstack-item-content-column-slider-item-child')
            );
    }, []);

    const setTweenFactor = useCallback((emblaApi) => {
        tweenFactor.current = TWEEN_FACTOR_BASE * emblaApi.scrollSnapList().length;
    }, []);

    const tweenEffects = useCallback((emblaApi, eventName) => {
        const engine = emblaApi.internalEngine();
        const scrollProgress = emblaApi.scrollProgress();
        const slidesInView = emblaApi.slidesInView();
        const isScrollEvent = eventName === 'scroll';

        emblaApi.scrollSnapList().forEach((scrollSnap, snapIndex) => {
            let diffToTarget = scrollSnap - scrollProgress;
            const slidesInSnap = engine.slideRegistry[snapIndex];

            slidesInSnap.forEach((slideIndex) => {
                if (isScrollEvent && !slidesInView.includes(slideIndex)) return;

                if (engine.options.loop) {
                    engine.slideLooper.loopPoints.forEach((loopItem) => {
                        const target = loopItem.target();

                        if (slideIndex === loopItem.index && target !== 0) {
                            const sign = Math.sign(target);

                            if (sign === -1) {
                                diffToTarget = scrollSnap - (1 + scrollProgress);
                            }
                            if (sign === 1) {
                                diffToTarget = scrollSnap + (1 - scrollProgress);
                            }
                        }
                    });
                }

                const tweenValue = 1 - Math.abs(diffToTarget * tweenFactor.current);
                const scale = numberWithinRange(tweenValue, 0, 1).toString();
                const opacity = numberWithinRange(tweenValue, 0, 1).toString();

                // Apply scale effect
                const tweenNode = tweenNodes.current[slideIndex];
                if (tweenNode) {
                    tweenNode.style.transform = `scale(${scale})`;
                }

                // Apply opacity effect
                emblaApi.slideNodes()[slideIndex].style.opacity = opacity;
            });
        });
    }, []);

    useEffect(() => {
        if (!emblaApi) return;

        setTweenNodes(emblaApi);
        setTweenFactor(emblaApi);
        tweenEffects(emblaApi);

        emblaApi
            .on('reInit', setTweenNodes)
            .on('reInit', setTweenFactor)
            .on('reInit', tweenEffects)
            .on('scroll', tweenEffects)
            .on('slideFocus', tweenEffects);
    }, [emblaApi, setTweenNodes, setTweenFactor, tweenEffects]);

    const slideDescriptions = [
        "We create stunning 3D models, animations, and realistic environments for immersive experiences.",
        "Dynamic motion graphics and cinematic visual effects brought to life with seamless precision.",
        "Precision-crafted designs and visuals with unmatched detail for polished, professional results.",
        "Professional-grade video editing and vibrant color grading for high-quality storytelling impact.",
        "Complex simulations and breathtaking VFX for cutting-edge creativity in every project.",
    ];

    return (
        <section className="techstack">
            <div className="techstack-content">
                <div className="textbox">
                    <div className="subheadline-box opacity-blur" ref={subheadlineBoxRef} >
                        <Layers className="subheadline-box-icon" />
                        <h2 className="small-description grey" >Our Techstack</h2>
                    </div>
                    <div className="titlebox">
                        <div className="titlebox-big-gradient" />
                        <h2 className="subheadline white" ref={titleRef} >Modern Tech Stack for <br className="hide-on-mobile" /> High-Performance Solutions</h2>
                    </div>
                    <p className="description grey" ref={descriptionRef} >Industry-leading tools integrated to deliver fast, scalable, and maintainable systems</p>
                </div>
                <div className="techstack-container">
                    <div className="techstack-item-big techstack-item-no-padding" ref={bentoBoxRef1} >
                        <div className="techstack-item-content">
                            <div className="techstack-item-content-center">
                                <div className="textbox">
                                    <h2 className="small-subheadline white hide-on-mobile" >Built with proven <br /> technologies and frameworks</h2>
                                    <button className="button hero-button button-transparent-border" onClick={handleVideoButtonClick} >
                                        <div className="button-content">
                                            <span className="small-description">View Tech Stack</span>
                                            <span className="small-description">View Tech Stack</span>
                                        </div>
                                        <div className="button-circle button-circle-white">
                                            <ArrowUpRight className="button-icon button-icon-180" />
                                        </div>
                                    </button>
                                </div>
                            </div>
                            <video className="techstack-item-content-video" ref={videoRef} src="/videos/logos.mp4" alt="Duotone" muted playsInline={true} data-wf-ignore="true" loop />
                        </div>
                        <div className="background-gradient-circle" />
                        <div className="techstack-item-no-padding-border" />
                    </div>
                    <div className="techstack-item-small techstack-item-small-mobile-big" ref={bentoBoxRef2} >
                        <div className="techstack-item-content">
                            <div className="techstack-item-content-column">
                                <div className="techstack-item-content-column-slider">
                                    <img src="/images/abs.webp" className="techstack-item-content-column-slider-image" alt="" />
                                    <div className="techstack-item-content-column-slider-carousel" ref={emblaRef} >
                                        <div className="techstack-item-content-column-slider-carousel-row">
                                            <div className="techstack-item-content-column-slider-item">
                                                <div className="techstack-item-content-column-slider-item-child">
                                                    <img src="/logos/blenderwhite.svg" className="techstack-item-content-column-slider-item-image" alt="" />
                                                </div>
                                            </div>
                                            <div className="techstack-item-content-column-slider-item">
                                                <div className="techstack-item-content-column-slider-item-child">
                                                    <img src="/logos/ae.svg" className="techstack-item-content-column-slider-item-image" alt="" />
                                                </div>
                                            </div>
                                            <div className="techstack-item-content-column-slider-item">
                                                <div className="techstack-item-content-column-slider-item-child">
                                                    <img src="/logos/photoshop.svg" className="techstack-item-content-column-slider-item-image" alt="" />
                                                </div>
                                            </div>
                                            <div className="techstack-item-content-column-slider-item">
                                                <div className="techstack-item-content-column-slider-item-child">
                                                    <img src="/logos/davinciresolvewhite.svg" className="techstack-item-content-column-slider-item-image" alt="" />
                                                </div>
                                            </div>
                                            <div className="techstack-item-content-column-slider-item">
                                                <div className="techstack-item-content-column-slider-item-child">
                                                    <img src="/logos/houdini.png" className="techstack-item-content-column-slider-item-image" alt="" />
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
